package com.twitter.producer.config;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.social.twitter.api.impl.TwitterTemplate;@Configurationpublic class TwitterConfig {    private final Logger log = LoggerFactory.getLogger(TwitterConfig.class);    private final TwitterProperties twitterProperties;    public TwitterConfig(TwitterProperties twitterProperties) {        this.twitterProperties = twitterProperties;    }    @Bean    public TwitterTemplate twitterTemplate() {        log.info("Configuring Twitter...");        return new TwitterTemplate(                twitterProperties.getAppId(),                twitterProperties.getAppSecret(),                twitterProperties.getAccessToken(),                twitterProperties.getAccessTokenSecret()        );    }}