package com.twitter.producer.config;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.stereotype.Component;/** * Twitter Properties specific to Twitter Social properties. * * <p> * Properties are configured in the application.yml file. * </p> */@ConfigurationProperties(prefix = "spring.social.twitter", ignoreUnknownFields = false)@Componentpublic class TwitterProperties {    private String appId;    private String appSecret;    private String accessToken;    private String accessTokenSecret;    public String getAppId() {        return appId;    }    public void setAppId(String appId) {        this.appId = appId;    }    public String getAppSecret() {        return appSecret;    }    public void setAppSecret(String appSecret) {        this.appSecret = appSecret;    }    public String getAccessToken() {        return accessToken;    }    public void setAccessToken(String accessToken) {        this.accessToken = accessToken;    }    public String getAccessTokenSecret() {        return accessTokenSecret;    }    public void setAccessTokenSecret(String accessTokenSecret) {        this.accessTokenSecret = accessTokenSecret;    }}